import React, { Component } from 'react';
import { Grid } from 'semantic-ui-react';
// import logo from './logo.svg';
import './App.css';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import SenderView from './senderView';
import ReceiverView from './receiverView';
class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      tab1:'',
      tab2:''
    }
  }

  render() {
    return (
        <div>
        <center><h1>KFS DEMO</h1></center>
            <Router>
            <div>
              <Grid columns={2} padded>
                <Link to="/sender"><Grid.Column onClick={() => this.setState({tab1:'black',tab2:'white'})} color={this.state.tab1}>View as Sender</Grid.Column></Link>
                <Link to="/receiver"><Grid.Column onClick={() => this.setState({tab1:'white',tab2:'black'})} color={this.state.tab2}>View as Receiver</Grid.Column></Link>
              </Grid>
                <Route path="/sender" component={SenderView} />
                <Route path="/receiver" component={ReceiverView} />
            </div>
        </Router>
      </div>
    );
  }
}

export default App;
