import React, { Component } from 'react';
import { Button, Form, Input, Message, Grid , TextArea, Radio } from 'semantic-ui-react';
// import logo from './logo.svg';
import './App.css';
import web3 from './ethereum/web3.js';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      sender: '',
      receipent:'',
      private: '',
      base64content: '',
      authErrorMessage: '',
      errorMessage: '',
      checked:false,
      mime:'',
      uploadedFile:'',
      hashMessage:'',
      visible: false 
    }
  }

  componentDidMount(){
    web3.eth.getAccounts().then((accounts, err) => {
      this.setState({sender: accounts[0]});

    });
  }

  handleDismiss = () => {
    this.setState({ visible: false });
  }

  sendRequest = () => {
    // axios.get('//204.48.21.88:3000/create?mime=data:text/plain&content=base64,
    //&senderPub=MHg3MTExRmVBOGVEZUZhMTEzMTFGMDA2QkI1Y0UxQTA0Yzg4OTg2OEE4&reciPub=MHhjYTg0MzU2OWUzNDI3MTQ0Y2VhZDVlNGQ1OTk5YTNkMGNjZjkyYjhl')
    // .then(function (response) {
    //   console.log(response);
    // })
    // .catch(function (error) {
    //   console.log(error);
    // });
    if(this.state.mime !=='' && this.state.base64content!=='' && this.state.uploadedFile==='') {
      console.log(window.btoa(this.state.sender));
      console.log(window.btoa(this.state.receipent));
      console.log(window.btoa(this.state.mime));
      console.log(window.btoa(this.state.base64content));
      axios.get('//204.48.21.88:3000/create?mime='+this.state.mime+'&content=base64,'+this.state.base64content+'&senderPub='
      +window.btoa(this.state.sender)+'&reciPub='+window.btoa(this.state.receipent))
      .then( response => {
        console.log(response);
        this.setState({hashMessage:response.data,visible:true})
      })
      .catch(function (error) {
        console.log(error);
      });
    }
    else if(this.state.mime ==='' && this.state.base64content==='' && this.state.uploadedFile!=='') {
      axios.get('//204.48.21.88:3000/create?path='+this.state.file+'&senderPub='
      +window.btoa(this.state.sender)+'&reciPub='+window.btoa(this.state.receipent))
      .then(response => {
        console.log(response);
        this.setState({hashMessage:response.data,visible:true})
      })
      .catch(function (error) {
        console.log(error);
      });
    }
  }

  senderView = () => {
    return (
        <div className="App">
          <header className="App-header">

              <h3>KFS DEMO</h3>

              <Grid>
                <Grid.Row>
                  <Grid.Column width={16}>
                    <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                    <Form.Field>
                      <h4>Sender's address</h4>
                      <Input disabled size="large" style={{ width: "100%"}}
                        value={this.state.sender}      
                      />
                    </Form.Field>
                    <Form.Field>
                      <h4>Enter Receipent's address</h4>
                      <Input style={{ width: "100%" }} size="large"
                      value={this.state.receipent}
                      onChange={event => this.setState({ receipent: event.target.value})}
                      />
                    </Form.Field>
                    {/* <Form.Field>
                      <h4>Enter Private key</h4>
                      <Input style={{ width: "100%" }}
                        value={this.state.private}
                        onChange={event => this.setState({ private: event.target.value})}
                      />
                    </Form.Field> */}
                    <Form.Field>
                    <Radio toggle
