import React, { Component } from 'react';
import { Button, Form, Input, Message, Grid , TextArea, Radio } from 'semantic-ui-react';
// import logo from './logo.svg';
import './App.css';
import web3 from './ethereum/web3.js';
import axios from 'axios';

class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      sender: '',
      receipent:'',
      private: '',
      base64content: '',
      authErrorMessage: '',
      errorMessage: '',
      checked:false,
      mime:'',
      uploadedFile:'',
      hashMessage:''
    }
  }

  componentDidMount(){
    web3.eth.getAccounts().then((accounts, err) => {
      this.setState({sender: accounts[0]});

    });
  }

  sendRequest = () => {
    // axios.get('//204.48.21.88:3000/create?mime=data:text/plain&content=base64,
    //&senderPub=MHg3MTExRmVBOGVEZUZhMTEzMTFGMDA2QkI1Y0UxQTA0Yzg4OTg2OEE4&reciPub=MHhjYTg0MzU2OWUzNDI3MTQ0Y2VhZDVlNGQ1OTk5YTNkMGNjZjkyYjhl')
    // .then(function (response) {
    //   console.log(response);
    // })
    // .catch(function (error) {
    //   console.log(error);
    // });
    if(this.state.mime !=='' && this.state.base64content!=='' && this.state.uploadedFile==='') {
      console.log(window.btoa(this.state.sender));
      console.log(window.btoa(this.state.receipent));
      console.log(window.btoa(this.state.mime));
      console.log(window.btoa(this.state.base64content));
      axios.get('//204.48.21.88:3000/create?mime='+this.state.mime+'&content=base64,'+this.state.base64content+'&senderPub='
      +window.btoa(this.state.sender)+'&reciPub='+window.btoa(this.state.receipent))
      .then(function (response) {
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
    }
    else if(this.state.mime ==='' && this.state.base64content==='' && this.state.uploadedFile!=='') {
      axios.get('//204.48.21.88:3000/create?path='+this.state.file+'&senderPub='
      +window.btoa(this.state.sender)+'&reciPub='+window.btoa(this.state.receipent))
      .then(function (response) {
        console.log(response);
        this.setState({hashMessage:response.data})
      })
      .catch(function (error) {
        console.log(error);
      });
    }
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">

            <h3>KFS DEMO</h3>
            <Grid>
              <Grid.Row>
                <Grid.Column width={16}>
                  <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
                  <Form.Field>
                    <h4>Sender's address</h4>
                    <Input disabled size="large" style={{ width: "100%"}}
                      value={this.state.sender}      
                    />
                  </Form.Field>
                  <Form.Field>
                    <h4>Enter Receipent's address</h4>
                    <Input style={{ width: "100%" }} size="large"
                    value={this.state.receipent}
                    onChange={event => this.setState({ receipent: event.target.value})}
                    />
                  </Form.Field>
                  {/* <Form.Field>
                    <h4>Enter Private key</h4>
                    <Input style={{ width: "100%" }}
                      value={this.state.private}
                      onChange={event => this.setState({ private: event.target.value})}
                    />
                  </Form.Field> */}
                  <Form.Field>
                  <Radio toggle
                    label={this.state.checked ? 'Would like to send a file' : 'Would like to send base64 content'}
                    onClick={() => 
                      this.setState({checked: !this.state.checked})
                    } 
                    checked={this.state.checked} />
                  </Form.Field>             
                {/* {this.state.checked ?  */}
                    <Form.Field>
                      <h4>Enter content's mime here</h4>
                      <Input style={{ width: "100%" }}
                        value={this.state.mime}
                        onChange={event => this.setState({ mime: event.target.value})}
                      />
                    </Form.Field>
                    <Form.Field>
                    <h4>Enter base64 content here</h4>
                     <TextArea placeholder='Base64 content ' 
                        value={this.state.base64content}
                        onChange={event => this.setState({ base64content: event.target.value})}
                        />
                    </Form.Field>




                  <div className="upload-btn-wrapper" style={{fontSize:''}}>
                    <br/>
                    <b>Upload a file</b>
                      {/* <button className="btn-primary">Upload */}
                      <input type="file" name="myfile"  onChange={event => this.setState({uploadedFile : event.target.files[0]})}/>
                      {/* </button> */}
                  </div> 
                  <Message error header="Oops!" hidden={true} onDismiss={this.errorMessageDismiss} content={this.state.errorMessage} />
                  <Button loading={this.state.submitButton}  onClick={this.sendRequest} primary>Submit</Button>
                </Form>
              </Grid.Column>
            </Grid.Row>
          </Grid>
        </header>
        <Message success header="Hash" hidden={this.state.hashMessage === ""} onDismiss={this.errorMessageDismiss} content={this.state.hashMessage} />
      </div>
    );
  }
}

export default App;
